import NInput from "./NInput";
import {useEffect, useState} from "react";
import {Animated, Easing, View} from "react-native";

export default function UIView() {
    const [n, setN] = useState();
    let squaresWidth;
    let squaresHeight;

    const setDimensions = (layout) => {
        const {x, y, width, height} = layout;
        squaresWidth = width;
        squaresHeight = height;
    }
    const onStart = (n) => {
        setN(n)
    }
    return (
        <View style={{flex: 1, justifyContent: 'center', alignItems: 'center'}} onLayout={(event) => {
            setDimensions(event.nativeEvent.layout)
        }}>
            {n > 0 ? <RotatingSquares n={n} width={squaresWidth} height={squaresHeight}></RotatingSquares> :
                <NInput onStart={onStart}></NInput>}
        </View>
    )
}

function RotatingSquares({n, width, height}) {
    let sizePerSquare = width * height / n;
    let squareSideLength = Math.sqrt(sizePerSquare);
    let smallestSideLength =
        Math.min(height, width);
    if (smallestSideLength > squareSideLength) {
        smallestSideLength =
            smallestSideLength / Math.ceil(smallestSideLength / squareSideLength);
    }
    squareSideLength = smallestSideLength;
    let rows = Math.ceil(height / squareSideLength);
    let columns = Math.ceil(width / squareSideLength);
    console.log(rows, columns)
    return (
        <View style={{flex: 1, justifyContent: "center", alignItems: "center", width: width, height: height}}>
            <View style={{flex: 1, justifyContent: "center", alignItems: "center", flexDirection: "row"}}>
                {Array.from({length: columns}, () => (
                    <View>
                        {Array.from({length: rows}, () => {
                            if (n > 0) {
                                n--;
                                return (
                                    <RotatingSquare length={squareSideLength}></RotatingSquare>
                                );
                            } else {
                                return (
                                    <View style={{width: squareSideLength, height: squareSideLength}}/>
                                );
                            }
                        })}
                    </View>
                ))}
            </View>
        </View>
    )
}

function RotatingSquare({length}) {
    const rotationValue = new Animated.Value(0);

    useEffect(() => {
        startRotation();
    }, []);

    const startRotation = () => {
        Animated.loop(
            Animated.timing(rotationValue, {
                toValue: 1,
                duration: 2000,
                easing: Easing.linear,
                useNativeDriver: true,
            })
        ).start();
    };

    const interpolatedRotation = rotationValue.interpolate({
        inputRange: [0, 1],
        outputRange: ['0deg', '360deg'],
    });

    return (
        <View style={{flex: 1, alignItems: 'center', justifyContent: 'center'}}>
            <Animated.View
                style={{
                    width: length,
                    height: length,
                    backgroundColor: 'blue',
                    transform: [{rotate: interpolatedRotation}],
                }}
            />
        </View>
    );
};
