import NInput from "./NInput";
import {useState} from "react";
import {Text} from "react-native-web";
import {View} from "react-native";

export default function CalculationView() {
    const [n, setN] = useState();
    const fibonacciAlgorithm = (n) => {
        return 1;

    }
    const matrixMultiplicationAlgorithm = (n) => {
        return 1;

    }
    const binarySearchTreeAlgorithm = (n) => {
        return 1;
    }
    const reverseArrayAlgorithm = (n) => {
        return 1;

    }
    return (
        <View style={{ flex: 1, justifyContent: 'center', alignItems: 'center' }}>
            <CalculationWidget name="Fib" algorithmImplmentation={fibonacciAlgorithm}></CalculationWidget>
            <CalculationWidget name="Matrix" algorithmImplmentation={matrixMultiplicationAlgorithm}></CalculationWidget>
            <CalculationWidget name="Binary" algorithmImplmentation={binarySearchTreeAlgorithm}></CalculationWidget>
            <CalculationWidget name="Array" algorithmImplmentation={reverseArrayAlgorithm}></CalculationWidget>
        </View>
    )
}

function CalculationWidget({name, algorithmImplmentation}) {
    const [executionTime, setExecutionTime] = useState(null);
    const [n, setN] = useState();
    const onStart = (n) => {
        algorithmImplmentation(n);
        setN(n);
    }
    return (
        <View style={{flexDirection: "row"}}>
            <Text>{name}:</Text>
            <NInput onStart={onStart}></NInput>
            {executionTime != null && <Text>{executionTime}Î¼s; n = {n}</Text>}
        </View>
    )
}
